INSERT INTO `categories_description` (`categories_id`, `language_id`, `categories_name`, `categories_description`) VALUES
(1, 1, 'Television', '<p style=\"margin-left:24px\"><span style=\"font-size:12pt\"><span style=\"font-family:Times\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Spring isn&#39;t just known for warmer weather and great college ball, it&#39;s when the newest crop of HDTVs is released. Check out what&#39;s available now and research those HDTVs that will be arriving later this spring. Browse brands such as Panasonic, Samsung, Sony, Toshiba, and more.</span></span></span></p>'),
(2, 1, 'Camera', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Capture life&#39;s most memorable moments to share with family and friends for years to come. Our wide selections of camera products make recording memories easy and fun</span></span></p>'),
(3, 1, 'Computers', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">The right computer can be the center of your digital home &mdash; everyday tasks and homework, plus photos, music and games.</span></span></p>'),
(4, 1, 'Desktop', '<p style=\"margin-left:18px\"><span style=\"font-size:12pt\"><span style=\"font-family:Times\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Whether you need computer for </span><b><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Photos &amp; Music or Entertainment or Gaming, </span></b><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">we got it. Look no further. We offer computers with lots of </span><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">hard drive space for multimedia and everyday computing. Ample amount of memory to edit photos and videos. High-quality graphics with dedicated video memory to bring movies and games to life. Quad-core processing or better. Enhanced graphics and audio for eye-popping visuals and rich, full sound</span></span></span></p>'),
(5, 1, 'Laptop', '<p style=\"margin-left:24px\"><span style=\"font-size:12pt\"><span style=\"font-family:Times\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Powered by the latest ultra-low-voltage Intel processors, these cutting-edge laptops offer extended battery life at budget-friendly prices.</span></span></span></p>'),
(6, 1, 'Chains', '<p>Shop the newest and flashiest chains on the market to keep your style looking fresh.&nbsp;</p>'),
(7, 1, 'Test', ''),
(8, 1, 'Print Document', ''),
(9, 1, 'Music CD', '');
