(631, 'Enable PayPal Module', 'MODULE_PAYMENT_PAYPAL_STATUS', 'True', 'Do you want to accept PayPal payments?', 6, 0, NULL, '2023-05-20 00:57:27', NULL, 'zen_cfg_select_option(array(\'True\', \'False\'), ', NULL),
(632, 'Business ID', 'MODULE_PAYMENT_PAYPAL_BUSINESS_ID', 'gibson@igjewl.bytehost18.com', 'Primary email address for your PayPal account.<br>NOTE: This must match <strong>EXACTLY </strong>the primary email address on your PayPal account settings.  It <strong>IS case-sensitive</strong>, so please check your PayPal profile preferences at paypal.com and be sure to enter the EXACT same primary email address here.', 6, 2, NULL, '2023-05-20 00:57:27', NULL, NULL, NULL),
(633, 'Transaction Currency', 'MODULE_PAYMENT_PAYPAL_CURRENCY', 'Only USD', 'Which currency should the order be sent to PayPal as? <br>NOTE: if an unsupported currency is sent to PayPal, it will be auto-converted to USD.', 6, 3, NULL, '2023-05-20 00:57:27', NULL, 'zen_cfg_select_option(array(\'Selected Currency\', \'Only USD\', \'Only AUD\', \'Only CAD\', \'Only EUR\', \'Only GBP\', \'Only CHF\', \'Only CZK\', \'Only DKK\', \'Only HKD\', \'Only HUF\', \'Only JPY\', \'Only NOK\', \'Only NZD\', \'Only PLN\', \'Only SEK\', \'Only SGD\', \'Only THB\', \'Only MXN\', \'Only ILS\', \'Only PHP\', \'Only TWD\', \'Only BRL\', \'Only MYR\', \'Only TRY\', \'Only INR\'), ', NULL),
