(635, 'Set Pending Notification Status', 'MODULE_PAYMENT_PAYPAL_PROCESSING_STATUS_ID', '1', 'Set the status of orders made with this payment module that are not yet completed to this value<br>(\'Pending\' recommended)', 6, 5, NULL, '2023-05-20 00:57:27', 'zen_get_order_status_name', 'zen_cfg_pull_down_order_statuses(', NULL),
